import React, { Component } from 'react';
import './App.css';

class Box extends Component {
  constructor(props){
    super(props)
    this.state = {
      value: this.props.value,
      userInput: this.props.userInput
    }
  }
  handleClick = () => {
    const { userInput } = this.state
    return this.setState({userInput: "X" ? "O" : "X"})
  }

  // handleTurn = (e) => {
