import React, { Component } from 'react';
import './App.css';
import Box from './Box.js'

class User extends Component {
  constructor(props){
    super(props)
      this.state = {
        board: [0, 0, 0, 0, 0, 0, 0, 0, 0],
        count: 1,
      }
  }
  // handleOddClick = () => {
  // let { userInput } = this.state
  // this.setState({userInput: "X"})
  // }
  // handleEvenClick = () => {
  //   let { userInput } = this.state
  //   this.setState({userInput: "O"})
  // }
  handleClick = () => {
    let { count } = this.state
    if (count % 2 !== 0){
      <Box value="X" />
    } else {
      <Box value="O" />
    }
  }

  render(){
    let { board } = this.state
    let gameBoard = board.map((value, index) => {
      return (
        <Box
          value={value}
          index={index}
        />
      )
    })
    return (
      <div className="App">
        {gameBoard}
      </div>
    )
  }
}

export default User;
