import React, { Component } from 'react';
import './App.css';

class Box extends Component {
  constructor(props){
    super(props)
    this.state = {
      userInput: ""
    }
  }
  handleClick = () => {
    let { board, count, value } = this.props
    if (value === 0){
      //Need to replace the value for each Box to "X" or "O" to have it display on gameBoard
      this.setState({value: "X"})
    } else {
      this.setState({value: "O"})
    }
  }

  // handleTurn = (e) => {
  //   let { count } = this.props
  //   let newCount = count + 1
  //   if (e.target.value){
  //     this.setState({count: newCount})
  //   }
  // }

  render(){
    const { userInput } = this.state
    let { value, handleClick } = this.props
    return (
      <div>
        <div id="mainBox" onClick={this.handleClick}>
          {this.userInput}
        </div>
      </div>
    )
  }
}

export default Box;
