import React, { Component } from 'react';
import './App.css';
import Box from './Box.js'

class User extends Component {
  constructor(props){
    super(props)
    this.state = {
        board: ["", "","", "", "", "", "", "", ""],
        count: 1
      }
  }

  // handleUser = () => {
  //   const { user } = this.state
  //   this.setState({user: user === "X" ? "O" : "X"})
  // }


  handleClick = (boxIndex) => {
    const { board, count } = this.state
    count % 2 !== 0 ? board[boxIndex] = "X" : board[boxIndex] = "O"
    // board[boxIndex] = "X"
    this.setState({ board })
  }


//using .map()
  render(){
    const { board, user } = this.state
    const gameBoard = board.map((value, index) => {
      return (
        <Box
          value={value}
          index={index}
          user={user}
          handleUser={this.handleUser}
          handleClick={this.handleClick}
          board={board}
        />
      )
    })
    return (
      <div>
        <div id="gameBoard">
          {gameBoard}
        </div>
      </div>
    )
  }
}

export default User;
