import React, { Component } from 'react';
import './App.css';
import Box from './Box.js'

class User extends Component {
  constructor(props){
    super(props)
      this.state = {
        board: [this.userInput, this.userInput, this.userInput, this.userInput, this.userInput, this.userInput, this.userInput, this.userInput, this.userInput],
        count: 1,
        //create a value that is set to an empty string that replaces the value in board
        userInput: ""
      }
  }
  // handleOddClick = () => {
  // let { userInput } = this.state
  // this.setState({userInput: "X"})
  // }
  // handleEvenClick = () => {
  //   let { userInput } = this.state
  //   this.setState({userInput: "O"})
  // }

  handleClick = () => {
    let { board, count, userInput } = this.state
    if (count % 2 !== ""){
      //Need to replace the value for each Box to "X" or "O" to have it display on gameBoard
      return <Box value="X" />
    } else {
      return <Box value="O" />
    }
  }

  //create new function that takes in synthetic event and use e.target.value and e.target.src

  render(){
    let { board } = this.state
    let gameBoard = board.map((value, index) => {
      return (
        <Box
          value={value}
          index={index}
          handleClick={this.handleClick}
        />
      )
    })
    return (
      <div>
        <div id="gameBoard">
          {gameBoard}
        </div>
      </div>
    )
  }
}

export default User;
