import React, { Component } from 'react';
import './App.css';
import Box from './Box.js'

class User extends Component {
  constructor(props){
    super(props)
    this.state = {
        board: ["", "","", "", "", "", "", "", ""],
        user: "X"
      }
  }

  handleUser = () => {
    const { user } = this.state
    this.setState({user: user === "X" ? "O" : "X"})
  }


  render(){
    const { board, user } = this.state
    const gameBoard = board.map((value, index) => {
      return (
        <Box
          value={value}
          index={index}
          handleUser={this.handleUser}
          gameBoard={this.gameBoard}
        />
      )
    })
    return (
      <div>
        <div id="gameBoard">
          {gameBoard}
        </div>
      </div>
    )
  }
}

export default User;
