import React, { Component } from 'react';
import './App.css';

class Box extends Component {
  constructor(props){
    super(props)
    this.state = {
      value: this.props.value,
      // user: this.props.user
    }
  }
  handleTurn = () => {
    const { user } = this.props
    this.setState({user: user === "X" ? "O" : "X"})
  }
  handleClick = () => {
    const { user } = this.props
    this.handleTurn()
    this.setState({value: user})
  }

  render(){
    const { value } = this.state
    return (
      <div>
        <div id="mainBox" onClick={this.handleClick}>
          {value}
        </div>
      </div>
    )
  }
}

export default Box;
