import React, { Component } from 'react';
import './App.css';
import Flexbox from 'flexbox-react'

class Box extends Component {
  handleClick = () => {
    let { board, count, value } = this.props
    let newCount = count + 1
    if (count % 2 !== 0){
      this.setState({count: newCount})
      //Need to replace the value for each Box to "X" or "O" to have it display on gameBoard
      this.setState({board: <Box value="X" />})
    } else {
      this.setState({count: newCount})
      this.setState({board: <Box value="O" />})
    }
  }

  render(){
    let { value, handleClick } = this.props
    return (
      <div>
        <div id="mainBox" onClick={handleClick}>
          {value}
        </div>
      </div>
    )
  }
}

export default Box;
