  //     board.push("X")
  //   } else {
  //     board.push("O")
  //   }
  // }

  handleTurn = (e) => {
    let { count } = this.props
    let newCount = count + 1
    if (e.target.value){
      this.setState({count: newCount})
    }
  }

  render(){
    let { value } = this.props
    return (
      <div>
        <div id="mainBox" onClick={this.handleClick}>
          {value}
        </div>
      </div>
    )
  }
}

export default Box;
