  // } else if (gameBoard[0] === gameBoard[4] && gameBoard[4] === gameBoard[8]) {
  //   alert(`${value} is the winner!`)
  // }

  render(){
    const { value } = this.state
    return (
      <div>
        <div id="mainBox" onClick={this.handleClick}>
          {value}
        </div>
      </div>
    )
  }
}

export default Box;
