import React, { Component } from 'react';
import './App.css';
import Flexbox from 'flexbox-react'

class Box extends Component {
  handleClick = () => {
    let { count, value } = this.props
    let newCount = count + 1
    if (count % 2 !== 0){
      this.setState({count: newCount})
      //Need to replace the value for each Box to "X" or "O" to have it display on gameBoard
      return value = "X"
    } else {
      this.setState({count: newCount})
      return value = "O"
    }
  }

  render(){
    let { value } = this.props
    return (
      <div>
        <div id="mainBox" onClick={this.handleClick}>
          {value}
        </div>
      </div>
    )
  }
}

export default Box;
