import React, { Component } from 'react';
import './App.css';

class Box extends Component {
  constructor(props){
    super(props)
    this.state = {
      value: this.props.value,
      index: this.props.index,
    }
  }

  handleClick = () => {
    const { value } = this.state
    const { user, handleUser, handleClick, index } = this.props
    handleClick(index)
    // handleUser()
    // if (value !== "X" && value !== "O"){
    // }
    // this.setState({value: user})
    this.checkWinner()
    console.log(value)
  }


  checkWinner = () => {
    const { value, index, board } = this.props
    const wins = [
      [0, 1, 2],
      [0, 3, 6],
      [0, 4, 8],
      [1, 4, 7],
      [2, 5, 8],
      [3, 4, 5],
      [6, 7, 8],
      [2, 4, 6]
    ]
    for (let i=0; i<wins.length; i++){
      const [a, b, c] = wins[i]
      if (board[a] && board[a] === board[b] && board[a] === board[c]){
        alert(`${value} is the winner!`)
      }
    }
  }


  render(){
    const { value } = this.props
    return (
      <div>
        <div id="mainBox" onClick={this.handleClick}>
          {value}
        </div>
      </div>
    )
  }
}

export default Box;
