import React, { Component } from 'react';
import './App.css';
import Box from './Box.js'

class User extends Component {
  constructor(props){
    super(props)
      this.state = {
        board: [0, 0, 0, 0, 0, 0, 0, 0, 0],
        count: 1,

      }
  }
  // handleOddClick = () => {
  // let { userInput } = this.state
  // this.setState({userInput: "X"})
  // }
  // handleEvenClick = () => {
  //   let { userInput } = this.state
  //   this.setState({userInput: "O"})
  // }
  handleClick = () => {
    let { board, count } = this.state
    if (count % 2 !== 0){
      let newCount = count + 1
      this.setState({count: newCount})
      //Need to replace the value for each Box to "X" or "O" to have it display on gameBoard
      this.setState({board: ["X"]})
    } else {
      let newCount = count + 1
      this.setState({count: newCount})
      this.setState({board: ["O"]})
    }
  }

  render(){
    let { board } = this.state
    let gameBoard = board.map((value, index) => {
      return (
        <Box
          value={value}
          index={index}
          handleClick={this.handleClick}
        />
      )
    })
    return (
      <div>
        <div id="gameBoard">
          {gameBoard}
        </div>
      </div>
    )
  }
}

export default User;
